mapper.select:
select *
from t_favorite
where product_id = #{product_id}
  and user_id = #{user_id}

select p.id, p.title, p.price, p.image
from t_favorite f
         inner join t_product p on f.product_id = p.id
where user_id = #{user_id} limit #{offSet}
    , #{pageSize}

select count(1)
from t_favorite
where user_id = #{user_id}
